mapper.select:
SELECT COUNT(id) as cnt
FROM member
WHERE id=#{id}

SELECT adminno, name, id, password, rdate
FROM admin
WHERE adminno = #{adminno}

SELECT adminno, name, id, password, rdate
FROM admin
WHERE id = #{id}

mapper.update:
UPDATE admin 
SET name=#{name}
WHERE adminno=#{adminno}

mapper.select:
SELECT COUNT(adminno) as cnt
FROM admin
WHERE id=#{id} AND password=#{password}

SELECT COUNT(adminno) as cnt
FROM admin
WHERE adminno=#{adminno} AND password=#{password}

mapper.update:
UPDATE admin
SET password=#{password}
WHERE adminno=#{adminno}
