mapper.select:
SELECT COUNT(memberno) as cnt
FROM member
WHERE id=#{id}

SELECT COUNT(memberno) as cnt
FROM member
WHERE id=#{id} AND password=#{password}

mapper.update:
UPDATE member 
SET password=#{password}, ninckname=#{ninckname}, tel=#{tel}, address1=#{address1}, address2=#{address2}, email=#{email}
WHERE memberno=#{memberno}

mapper.select:
SELECT *
FROM member
WHERE name=#{name} AND tel=#{tel}

SELECT *
FROM member
WHERE name=#{name} AND id=#{id}

mapper.update:
UPDATE member 
SET password=#{password}
WHERE memberno=#{memberno}
