mapper.select:
SELECT m.id, r.replyno, r.contents, r.rdate, r.photo, r.communityno, r.memberno, r.photo1saved, r.thumb1, r.filesize, likecnt
FROM member m,  reply r
WHERE m.memberno = r.memberno and r.communityno = #{communityno};

mapper.update:
UPDATE reply 
set contents = #{contents}
WHERE replyno = #{replyno}

UPDATE reply 
set photo = #{photo}, photo1saved=#{photo1saved}, thumb1=#{thumb1}, filesize=#{filesize}
WHERE replyno = #{replyno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM reply
WHERE communityno = #{communityno}

mapper.update:
UPDATE reply
SET likecnt = likecnt + 1
WHERE replyno = #{replyno}
