mapper.update:
UPDATE reply 
set contents = #{contents}
WHERE replyno = #{replyno}

UPDATE reply 
set photo = #{photo}, photo1saved=#{photo1saved}, thumb1=#{thumb1}, filesize=#{filesize}
WHERE replyno = #{replyno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM reply
WHERE communityno = #{communityno}
