mapper.update:
UPDATE rereply 
set contents = #{contents}
WHERE rereplyno = #{rereplyno}

UPDATE rereply 
set photo = #{photo}, photo1saved=#{photo1saved}, thumb1=#{thumb1}, filesize=#{filesize}
WHERE rereplyno = #{rereplyno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM rereply
WHERE replyno = #{replyno}

mapper.update:
UPDATE rereply
SET likecnt = likecnt + 1
WHERE rereplyno = #{rereplyno}
